import React, { Component } from 'react'

export class NewsItem extends Component {
  render() {
    let {title,discription,imageUrl,newsUrl,author, date}=this.props;
    return (
      <div className = "my-3">
        <div className="card">
            <img src={!imageUrl?"https://kchanews.com/wp-content/uploads/2014/09/bigstock-Breaking-News-Screen-36237841.jpg":imageUrl} className="card-img-top" alt="..."/>
            <div className="card-body">
              <h5 className="card-title">{title}  <span class="position-absolute top-0 start-100 translate-middle badge rounded-pill bg-danger">
    99+
    <span class="visually-hidden">unread messages</span>
  </span> </h5>
              <h1 className="card-text">{discription}...</h1>
              <p className="card-text"><small class="text-body-secondary">By {!author?"Unknown":author} On {new Date(date).toGMTString()}</small></p>
              <a rel="noreferrer" href={newsUrl} target="_blank" className="btn btn-sm btn-dark">Read</a>
            </div>
          </div>
      </div>
    )
  }
}
export default NewsItem
