import React, { Component } from 'react'

export class NewsItem extends Component {
  render() {
    let {title,discription,imageUrl,newsUrl,author, date,source}=this.props;
    return (
      <div className = "my-3">
        <div className="card">
        <div style={{
                        display: 'flex',
                        justifyContent: 'flex-end',
                        position: 'absolute',
                        right: '0'
                    }
                    }>
       <span className="badge rounded-pill bg-danger"> {source} </span>
                    </div><img src={!imageUrl?"https://kchanews.com/wp-content/uploads/2014/09/bigstock-Breaking-News-Screen-36237841.jpg":imageUrl} className="card-img-top" alt="..."/>
            <div className="card-body">
              <h5 className="card-title">{title}</h5>
              <p className="card-text">{discription}</p>
              <p className="card-text"><small className="text-body-secondary">By {!author?"Unknown":author} On {new Date(date).toGMTString()}</small></p>
              <a rel="noreferrer" href={newsUrl} target="_blank" className="btn btn-sm btn-dark">Read More</a>
            </div>
          </div>
      </div>
    )
  }
}
export default NewsItem
