import React, { Component } from 'react'
import NewsItem from './NewsItem'

export class News extends Component {
  articles=[
  ]
  constructor(){
    super();
    this.state={
      articles: this.articles,
      loading: false,
      page:1
    }
  }

  async componentDidMount(){
    console.log("cmd");
    let url = "https://newsapi.org/v2/top-headlines?country=in&apiKey=ee9547e95cfc4b7c95f5533590212a24&page=1&pagesize=20";
    let data = await fetch(url);
    let parsedData = await data.json();
    console.log(parsedData);
    this.setState({articles: parsedData.articles,totalResults:parsedData.totalResults})
  }

   handlePrevClick= async ()=>{
    console.log("pre")
    let url = `https://newsapi.org/v2/top-headlines?country=in&apiKey=ee9547e95cfc4b7c95f5533590212a24&page=${this.state.page - 1}&pagesize=20`;
    let data = await fetch(url);
    let parsedData = await data.json();
    console.log(parsedData);
    this.setState({
      page:this.state.page - 1,
      articles: parsedData.articles
    })

  }
  handleNextClick= async  ()=>{
    console.log("next")
    let url = `https://newsapi.org/v2/top-headlines?country=in&apiKey=ee9547e95cfc4b7c95f5533590212a24&page=${this.state.page+1}&pagesize=20`;
    let data = await fetch(url);
    let parsedData = await data.json();
    console.log(parsedData);
    this.setState({
      page:this.state.page+1,
      articles: parsedData.articles
    })
  }
  

  render() {
    console.log("render")
    return (
          <div className ="container my-3">
            <h2>News Update - Top Headlines</h2>
            <div className="row" >
            {this.state.articles.map((element)=>{
                return <div className= "col-md-4" key={element.url}>
                  <NewsItem  title = {element.title?element.title:""} discription = {element.discription?element.description:""} imageUrl={element.urlToImage} newsUrl={element.url}/>
                </div>
            })}
            </div>
            <div className="container d-flex justify-content-between">
              <button disabled={this.state.page<=1} type="button" class="btn btn-dark" onClick={this.handlePrevClick}> &larr; Previous</button>
              <button type="button" class="btn btn-dark" onClick={this.handleNextClick}>Next &rarr; </button>
            </div>
          </div>
    )
  }
}

export default News
